                value={url}
                onChange={(e) => setUrl(e.target.value)}
            />
            <button onClick={handleCheckSecurity} disabled={loading}>
                {loading ? "ƒêang ki·ªÉm tra..." : "Ki·ªÉm tra ngay"}
            </button>

            {results.length > 0 && (
                <div className="ranking">
                    <h3>üèÜ B·∫£ng x·∫øp h·∫°ng b·∫£o m·∫≠t</h3>
                    <table>
                        <thead>
                            <tr>
                                <th>Th·ª© h·∫°ng</th>
