import React, { useState } from "react";
import axios from "axios";
import "./SecurityChecker.css";

const SecurityChecker = () => {
    const [url, setUrl] = useState("");
    const [result, setResult] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleCheckSecurity = async () => {
        if (!url) return alert("Vui l√≤ng nh·∫≠p URL");

        setLoading(true);
        try {
            const response = await axios.post("http://localhost:5000/check-security", { url });
            setResult(response.data);
        } catch (error) {
            setResult({ error: "Kh√¥ng th·ªÉ ki·ªÉm tra URL n√†y" });
        }
        setLoading(false);
    };

    return (
        <div className="security-checker">
            <h2>üîç Ki·ªÉm tra b·∫£o m·∫≠t Website</h2>
            <input
                type="text"
                placeholder="Nh·∫≠p URL c·∫ßn ki·ªÉm tra..."
                value={url}
                onChange={(e) => setUrl(e.target.value)}
            />
            <button onClick={handleCheckSecurity} disabled={loading}>
                {loading ? "ƒêang ki·ªÉm tra..." : "Ki·ªÉm tra ngay"}
            </button>

            {result && (
                <div className="result">
                    {result.error ? (
                        <p className="error">{result.error}</p>
                    ) : (
                        <>
                            <h3>üìå K·∫øt qu·∫£:</h3>
                            <p><strong>URL:</strong> {result.url}</p>
                            <p 
                                className={
                                    result.securityScore >= 80 ? "high-score" : result.securityScore >= 50 ? "medium-score" : "low-score"
                                }
                            >
                                <strong>üîê ƒêi·ªÉm b·∫£o m·∫≠t:</strong> {result.securityScore}
                            </p>
                            <h4>C√°c v·∫•n ƒë·ªÅ b·∫£o m·∫≠t:</h4>
                            <ul>
                                {result.issues.map((issue, index) => (
                                    <li key={index}>
                                        <p className="issue">{issue.message}</p>
                                        <pre className="code">{issue.solution}</pre>
                                    </li>
                                ))}
                            </ul>
                        </>
                    )}
                </div>
            )}
        </div>
    );
};

export default SecurityChecker;
