import React, { useState } from "react";
import axios from "axios";
import "./SecurityChecker.css";

const SecurityChecker = () => {
    const [url, setUrl] = useState("");
    const [result, setResult] = useState(null);
    const [loading, setLoading] = useState(false);

    const getScoreColor = (score) => {
        if (score >= 80) return "green";
        if (score >= 50) return "orange";
        return "red";
    };

    const handleCheckSecurity = async () => {
        if (!url) return alert("Vui lòng nhập URL");

        setLoading(true);
        try {
            const response = await axios.post("http://localhost:5000/check-security", { url });
            setResult(response.data);
        } catch (error) {
            setResult({ error: "Không thể kiểm tra URL này" });
        }
        setLoading(false);
    };

    return (
        <div className="security-checker">
            <h2>🔍 Kiểm tra bảo mật Website</h2>
            <input
                type="text"
                placeholder="Nhập URL cần kiểm tra..."
                value={url}
                onChange={(e) => setUrl(e.target.value)}
            />
            <button onClick={handleCheckSecurity} disabled={loading}>
                {loading ? "Đang kiểm tra..." : "Kiểm tra ngay"}
            </button>

            {result && (
                <div className="result">
                    {result.error ? (
                        <p className="error">{result.error}</p>
                    ) : (
                        <>
                            <h3>📌 Kết quả:</h3>
                            <p><strong>URL:</strong> {result.url}</p>
                            <p>
                                <strong>🔐 Điểm bảo mật:</strong>
                                <span
                                    style={{ color: getScoreColor(result.securityScore), fontWeight: "bold" }}
                                >
                                    {" "}{result.securityScore}
                                </span>
                            </p>
                            <h4>Các vấn đề bảo mật:</h4>
                            <table>
                                <thead>
                                    <tr>
                                        <th>Tiêu chí</th>
                                        <th>Điểm trừ</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {result.issues.map((issue, index) => (
                                        <tr key={index} style={{ color: getScoreColor(100 - issue.penalty) }}>
                                            <td>{issue.message}</td>
                                            <td>-{issue.penalty}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </>
                    )}
                </div>
            )}
        </div>
    );
};

export default SecurityChecker;
